import './App.css';
import { useState } from "react";

function App() {

  const [task, setTask] = useState("");

  const displayInfo = () => {
    console.log(task);
  }

  return (
    <div className="App">
      <section className="task-input-section">
        <label>Enter Task</label>
        <input type="text" 
        onChange={(event) => setTask(event.target.value)}
        />
        <button onClick={ displayInfo }>Submit</button>
      </section>
    </div>
  );
}

export default App;
