import './App.css';
import { useState } from "react";
import Axios from "axios";

function App() {

  const [task, setTask] = useState("");
  const [taskList, setTaskList] = useState([]);

  const addTask = () => {
    Axios.post("http://localhost:3001/create", {task: task}).then(() => {
      setTaskList([...taskList, {task: task},])
      alert("Task added...")
      getTasks()
    });
  };

  const getTasks = () => {
    Axios.get("http://localhost:3001/tasks").then((response) => {
      setTaskList(response.data);
    })
  }

  const deleteTask = (id) => {
    Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
      setTaskList(taskList.filter((val) =>  {
        return val.id !== id
      }))
    });
  };

  return (
    <div className="App">
      <section className="task-input">
        <h1>Task Tracker</h1>
        <input type="text" placeholder="Enter task"
        onChange={(event) => setTask(event.target.value)}
        />
        <button className="btn" onClick={ addTask }>Add task</button>
        <button onClick={ getTasks } className="btn">Show tasks</button>
    </section>
    <section className="task-list">
        {taskList.map((val, key) => {
          return (
          <div key={val.id} className="card">
            <h2>{ val.task }</h2>
            <button className="btn" onClick={() => deleteTask(val.id)}>Delete</button>
            <button className="btn">Edit</button>
          </div>
          )
        })}
    </section>


    </div>
  );
}

export default App;
