import './App.css';
import { useState } from "react";
import Axios from "axios";

function App() {

  const [task, setTask] = useState("");
  const [taskList, setTaskList] = useState([]);

  const addTask = () => {
    Axios.post("http://localhost:3001/create", {task: task}).then(() => {
      setTaskList([...taskList, {task: task},])
      alert("Task added...")
      getTasks()
    });
  };

  const getTasks = () => {
    Axios.get("http://localhost:3001/tasks").then((response) => {
      setTaskList(response.data);
    })
  }

  return (
    <div className="App">
      <section className="task-input-section">
        <label>Enter Task</label>
        <input type="text" 
        onChange={(event) => setTask(event.target.value)}
        />
        <button onClick={ addTask }>Submit</button>
        <button onClick={ getTasks }>Show tasks</button>

        {taskList.map((val, key) => {
          return <div key={key} className="card"> { val.task_info } </div>
        })}
      </section>
    </div>
  );
}

export default App;
